mapper.select:
select d.*, c.name as category_name from sky_take_out.dish d
    left outer join sky_take_out.category c on d.category_id = c.id


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and d.status = #{status}


order by update_time desc

select * from sky_take_out.dish


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}


order by create_time desc
